import React, { useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter, Route, Routes, Navigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';

import Navbar from './components/navbar/Navbar';
import CategoryPage from './pages/category/CategoryPage';
import ProductsPage from './pages/products/ProductsPage';
import CartPage from './pages/cart/CartPage';
import ProductPage from './pages/product/ProductPage';

import { fetchProductsAsync } from './redux/productSlice';
import { fetchCategoriesAsync } from './redux/categorySlice';
import { CART_SLICE_ACTIONS } from './redux/cartSlice';


const App = () => {

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchProductsAsync());
    dispatch(fetchCategoriesAsync());
  }, [dispatch]);

  useEffect(() => {
    const cartItemsStorage = localStorage.getItem('cart');
    if (cartItemsStorage) {
      const cartItems = JSON.parse(cartItemsStorage);
      dispatch(CART_SLICE_ACTIONS.setCartItems(cartItems));
    }
  }, [dispatch]);

  return (
    <div className="app">
      <Navbar />

      <div className="container mt-5">
        <BrowserRouter>
          <Routes>
            <Route
              path='/products'
              element={<ProductsPage />}
            />
            <Route
              path='/products/:id'
              element={<ProductPage />}
            />
            <Route
              path='/categories/:id'
              element={<CategoryPage />}
            />
            <Route path='/cart' element={<CartPage />} />
            <Route path="/" element={<Navigate replace to="/products" />} />
          </Routes>
          </BrowserRouter>
      </div>
    </div>
  );
}

export default App;
