import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";
import Dropdown from "react-bootstrap/Dropdown"

/****************************************([{ NEW NAVBAR}])****************************************/
function Header() {
  return (
    <Navbar
      collapseOnSelect
      expand="lg"
      className="body-tertiary"
      variant="light"
      bg="warning"
    >
      <Container>
        <Navbar.Brand href="#home">Septiem Arche</Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="#features">Accueil</Nav.Link>
            <Nav.Link href="#pricing">Espace Client</Nav.Link>

            <Dropdown as={NavItem}>
              <Dropdown.Toggle as={NavLink}>Click to see moreâ€¦</Dropdown.Toggle>
              <Dropdown.Menu>
                <Dropdown.Item>Hello there!</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>

            <NavDropdown title="Link" id="navbarScrollingDropdown">
              <NavDropdown.Item href="#action3">Action</NavDropdown.Item>
              <NavDropdown.Item href="#action4">
                Another action
              </NavDropdown.Item>
              <NavDropdown.Divider />
            </NavDropdown>

            <NavDropdown title="Categories" id="collasible-nav-dropdown">
              <NavDropdown.Item href="#action/3.1">Livres</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2"> Dvds </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
              <NavDropdown.Divider />
              {/* <NavDropdown.Item href="#action/3.4">
                Separated link
              </NavDropdown.Item> */}
            </NavDropdown>
          </Nav>
          <Nav>
            <Nav.Link href="#deets">CONNEXION</Nav.Link>
            <Nav.Link eventKey={2} href="#memes">
              PANIER
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Header;

